mapper.select:
select count(store_id)
from t_sys_store
where is_delete = 0
and user_code = #{userCode}

    and store_id != #{storeId}

select count(store_id)
from t_sys_store
where is_delete = 0
and business_license = #{businessLicense}

    and store_id != #{storeId}

select a.store_id storeId, a.store_name storeName, a.telephone telephone, a.address address, b.user_name userName, a.user_code userCode, a.invite_code inviteCode, b.user_account userAccount from t_sys_store a LEFT JOIN t_sys_user b on a.user_code = b.user_code where a.is_delete = 0
and a.store_id like concat('%', #{storeId}, '%')
and a.store_name like concat('%', #{storeName}, '%')
and b.user_name like concat('%', #{userName}, '%')
and a.province_id = #{provinceId}
and a.city_id = #{cityId}
and a.area_id = #{areaId}
and a.user_code = #{userId}
